
// This file is automatically generated. Do not edit it directly.
import { createClient } from '@supabase/supabase-js';
import type { Database } from './types';

const SUPABASE_URL = import.meta.env.VITE_SUPABASE_URL;
const SUPABASE_ANON_KEY = import.meta.env.VITE_SUPABASE_ANON_KEY;

// Import the supabase client like this:
// import { supabase } from "@/integrations/supabase/client";

// Create Supabase client with anon key and explicit auth storage config
export const supabase = createClient<Database>(
  SUPABASE_URL, 
  SUPABASE_ANON_KEY,
  {
    auth: {
      persistSession: true,
      autoRefreshToken: true,
      storage: localStorage
    }
  }
);

// Function to get Supabase client with custom auth token
export const getSupabaseWithAuth = (jwt: string) => {
  return createClient<Database>(SUPABASE_URL, SUPABASE_ANON_KEY, {
    global: {
      headers: {
        Authorization: `Bearer ${jwt}`
      }
    },
    auth: {
      persistSession: true,
      autoRefreshToken: true,
      storage: localStorage
    }
  });
};

// Add auth listener and session management
export const initSupabaseAuth = () => {
  const { data: { subscription } } = supabase.auth.onAuthStateChange(
    (event, session) => {
      // Update session state when auth state changes
      console.log("Auth state changed", event, session);
    }
  );

  return subscription;
};

// Auth helper functions 
export const getSession = async () => {
  const { data, error } = await supabase.auth.getSession();
  return { data, error };
};

// Add a function to sign in anonymously to enable using RLS
export const signInAnonymously = async () => {
  const { data, error } = await supabase.auth.signInAnonymously();
  return { data, error };
};
